// Rental.jsx - VERSIÓN CORREGIDA
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import {
    Package, Users, DollarSign, AlertCircle,
    Search, Plus, X, Calendar, Clock,
    CheckCircle, Edit, Trash2, CreditCard,
    RotateCcw, FileText
} from 'lucide-react';
import './Rental.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001/api';

const CATEGORIES = {
    herramientas: { label: 'Herramientas', icon: '🔧' },
    equipos: { label: 'Equipos', icon: '⚙️' },
    vehiculos: { label: 'Vehículos', icon: '🚗' },
    espacios: { label: 'Espacios', icon: '🏢' },
    otros: { label: 'Otros', icon: '📦' }
};

const RentalSystem = () => {
    // Estados principales
    const [items, setItems] = useState([]);
    const [rentals, setRentals] = useState([]);
    const [statistics, setStatistics] = useState({
        total_items: 0,
        available_units: 0,
        active_rentals: 0,
        overdue_rentals: 0,
        total_revenue: 0,
        pending_revenue: 0
    });

    // Estados de modales
    const [modals, setModals] = useState({
        item: false,
        rental: false,
        return: false,
        payment: false
    });

    // Estados de formularios
    const [itemForm, setItemForm] = useState({
        name: '',
        description: '',
        category: 'herramientas',
        quantity: 1,
        rental_price: 0,
        image_url: ''
    });

    const [rentalForm, setRentalForm] = useState({
        item_id: '',
        customer_name: '',
        customer_id_number: '',
        customer_phone: '',
        customer_email: '',
        customer_address: '',
        rental_start: new Date().toISOString().split('T')[0],
        rental_end: new Date(Date.now() + 86400000).toISOString().split('T')[0],
        quantity_rented: 1,
        has_collateral: false,
        collateral_description: '',
        total_amount: 0,
        initial_deposit: 0,
        notes: ''
    });

    // Otros estados
    const [editingItem, setEditingItem] = useState(null);
    const [selectedRental, setSelectedRental] = useState(null);
    const [loading, setLoading] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const [notification, setNotification] = useState(null);

    // Cargar datos al montar el componente
    useEffect(() => {
        loadItems();
        loadRentals();
        loadStatistics();
    }, []);

    // Función para cargar items
    const loadItems = async () => {
        try {
            const response = await axios.get(`${API_URL}/rental-items/enhanced`);
            setItems(response.data || []);
        } catch (error) {
            console.error('Error loading items:', error);
            showNotification('Error al cargar artículos', 'error');
        }
    };

    // Función para cargar alquileres
    const loadRentals = async () => {
        try {
            const response = await axios.get(`${API_URL}/rentals/enhanced`);
            setRentals(response.data || []);
        } catch (error) {
            console.error('Error loading rentals:', error);
            showNotification('Error al cargar alquileres', 'error');
        }
    };

    // Función para cargar estadísticas
    const loadStatistics = async () => {
        try {
            const response = await axios.get(`${API_URL}/rentals/statistics`);
            setStatistics(response.data || statistics);
        } catch (error) {
            console.error('Error loading statistics:', error);
        }
    };

    // Función para mostrar notificaciones
    const showNotification = (message, type = 'success') => {
        setNotification({ message, type });
        setTimeout(() => setNotification(null), 3000);
    };

    // FUNCIÓN CORREGIDA: Manejador para crear/actualizar artículo
    const handleItemSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            // Preparar datos con la estructura correcta
            const itemData = {
                name: itemForm.name,
                description: itemForm.description || '',
                category: itemForm.category,
                daily_rate: parseFloat(itemForm.rental_price) || 0,
                weekly_rate: parseFloat(itemForm.rental_price) * 6 || 0,
                monthly_rate: parseFloat(itemForm.rental_price) * 25 || 0,
                quantity_total: parseInt(itemForm.quantity) || 1,
                quantity_available: parseInt(itemForm.quantity) || 1, // Al crear, todo está disponible
                status: 'disponible',
                image_url: itemForm.image_url || ''
            };

            if (editingItem) {
                // Si estamos editando, mantener la cantidad disponible actual
                const currentItem = items.find(item => item.id === editingItem.id);
                if (currentItem) {
                    itemData.quantity_available = currentItem.quantity_available;
                }

                await axios.put(`${API_URL}/rental-items/${editingItem.id}`, itemData);
                showNotification('Artículo actualizado exitosamente');
            } else {
                // Crear nuevo artículo
                await axios.post(`${API_URL}/rental-items`, itemData);
                showNotification('Artículo creado exitosamente');
            }

            await loadItems();
            closeModal('item');
            resetItemForm();
        } catch (error) {
            console.error('Error saving item:', error);
            const errorMessage = error.response?.data?.error || 'Error al guardar el artículo';
            showNotification(errorMessage, 'error');
        } finally {
            setLoading(false);
        }
    };

    // FUNCIÓN CORREGIDA: Manejador para crear alquiler
    const handleRentalSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            // Calcular el monto total basado en días y precio
            const startDate = new Date(rentalForm.rental_start);
            const endDate = new Date(rentalForm.rental_end);
            const days = Math.ceil((endDate - startDate) / (1000 * 60 * 60 * 24)) + 1;

            const selectedItem = items.find(item => item.id === parseInt(rentalForm.item_id));
            if (!selectedItem) {
                throw new Error('Artículo no encontrado');
            }

            let totalAmount = 0;
            if (days >= 30) {
                totalAmount = (days / 30) * selectedItem.monthly_rate;
            } else if (days >= 7) {
                totalAmount = (days / 7) * selectedItem.weekly_rate;
            } else {
                totalAmount = days * selectedItem.daily_rate;
            }

            totalAmount = totalAmount * parseInt(rentalForm.quantity_rented || 1);

            const rentalData = {
                ...rentalForm,
                item_id: parseInt(rentalForm.item_id),
                quantity_rented: parseInt(rentalForm.quantity_rented) || 1,
                total_amount: totalAmount,
                initial_deposit: parseFloat(rentalForm.initial_deposit) || 0
            };

            await axios.post(`${API_URL}/rentals`, rentalData);
            showNotification('Alquiler creado exitosamente');

            await Promise.all([loadRentals(), loadItems(), loadStatistics()]);
            closeModal('rental');
            resetRentalForm();
        } catch (error) {
            console.error('Error creating rental:', error);
            const errorMessage = error.response?.data?.error || 'Error al crear alquiler';
            showNotification(errorMessage, 'error');
        } finally {
            setLoading(false);
        }
    };

    // Función para eliminar artículo
    const handleDeleteItem = async (id) => {
        if (!window.confirm('¿Está seguro de eliminar este artículo?')) return;

        try {
            await axios.delete(`${API_URL}/rental-items/${id}`);
            showNotification('Artículo eliminado exitosamente');
            await loadItems();
        } catch (error) {
            console.error('Error deleting item:', error);
            const errorMessage = error.response?.data?.error || 'Error al eliminar artículo';
            showNotification(errorMessage, 'error');
        }
    };

    // Función para procesar devolución
    const handleReturnSubmit = async (e) => {
        e.preventDefault();
        setLoading(true);

        try {
            const returnData = {
                condition_notes: returnForm.notes || '',
                return_condition: returnForm.condition || 'bueno'
            };

            await axios.patch(`${API_URL}/rentals/${selectedRental.id}/return`, returnData);

            // Si hay pago final, registrarlo
            if (returnForm.final_payment > 0) {
                await axios.post(`${API_URL}/rentals/${selectedRental.id}/payments`, {
                    amount: returnForm.final_payment,
                    payment_method: 'efectivo',
                    notes: 'Pago al devolver'
                });
            }

            showNotification('Devolución registrada exitosamente');
            await Promise.all([loadRentals(), loadItems(), loadStatistics()]);
            closeModal('return');
        } catch (error) {
            console.error('Error processing return:', error);
            showNotification('Error al procesar devolución', 'error');
        } finally {
            setLoading(false);
        }
    };

    // Funciones auxiliares
    const openModal = (modalName, data = null) => {
        setModals(prev => ({ ...prev, [modalName]: true }));

        if (modalName === 'item' && data) {
            setEditingItem(data);
            setItemForm({
                name: data.name || '',
                description: data.description || '',
                category: data.category || 'herramientas',
                quantity: data.quantity_total || 1,
                rental_price: data.daily_rate || 0,
                image_url: data.image_url || ''
            });
        }

        if (modalName === 'return' && data) {
            setSelectedRental(data);
        }
    };

    const closeModal = (modalName) => {
        setModals(prev => ({ ...prev, [modalName]: false }));
        if (modalName === 'item') {
            resetItemForm();
            setEditingItem(null);
        }
    };

    const resetItemForm = () => {
        setItemForm({
            name: '',
            description: '',
            category: 'herramientas',
            quantity: 1,
            rental_price: 0,
            image_url: ''
        });
    };

    const resetRentalForm = () => {
        setRentalForm({
            item_id: '',
            customer_name: '',
            customer_id_number: '',
            customer_phone: '',
            customer_email: '',
            customer_address: '',
            rental_start: new Date().toISOString().split('T')[0],
            rental_end: new Date(Date.now() + 86400000).toISOString().split('T')[0],
            quantity_rented: 1,
            has_collateral: false,
            collateral_description: '',
            total_amount: 0,
            initial_deposit: 0,
            notes: ''
        });
    };

    const formatDate = (date) => {
        if (!date) return '-';
        return new Date(date).toLocaleDateString('es-ES');
    };

    const formatCurrency = (amount) => {
        return new Intl.NumberFormat('es-ES', {
            style: 'currency',
            currency: 'USD'
        }).format(amount || 0);
    };

    // Estado para el formulario de devolución
    const [returnForm, setReturnForm] = useState({
        condition: 'bueno',
        notes: '',
        final_payment: 0
    });

    // Estado para el formulario de pagos
    const [paymentForm, setPaymentForm] = useState({
        amount: 0,
        payment_method: 'efectivo',
        notes: ''
    });

    return (
        <div className="rental-container">
            {/* Notificaciones */}
            {notification && (
                <div className={`notification ${notification.type}`}>
                    {notification.message}
                </div>
            )}

            {/* Header con estadísticas */}
            <div className="rental-header">
                <h1>Sistema de Alquileres</h1>
                <div className="stats-grid">
                    <div className="stat-card">
                        <Package size={24} />
                        <div>
                            <span>{statistics.total_items}</span>
                            <label>Artículos</label>
                        </div>
                    </div>
                    <div className="stat-card">
                        <CheckCircle size={24} />
                        <div>
                            <span>{statistics.available_units}</span>
                            <label>Disponibles</label>
                        </div>
                    </div>
                    <div className="stat-card">
                        <Users size={24} />
                        <div>
                            <span>{statistics.active_rentals}</span>
                            <label>Alquilados</label>
                        </div>
                    </div>
                    <div className="stat-card">
                        <AlertCircle size={24} />
                        <div>
                            <span>{statistics.overdue_rentals}</span>
                            <label>Vencidos</label>
                        </div>
                    </div>
                </div>
            </div>

            {/* Sección de Artículos */}
            <div className="section">
                <div className="section-header">
                    <h2>Inventario de Artículos</h2>
                    <button
                        className="btn btn-primary"
                        onClick={() => openModal('item')}
                    >
                        <Plus size={20} />
                        Nuevo Artículo
                    </button>
                </div>

                <div className="items-grid">
                    {items.map(item => (
                        <div key={item.id} className="item-card">
                            {item.image_url && (
                                <img
                                    src={item.image_url}
                                    alt={item.name}
                                    className="item-image"
                                />
                            )}
                            <div className="item-content">
                                <h3>{item.name}</h3>
                                <p className="item-category">
                                    {CATEGORIES[item.category]?.icon} {CATEGORIES[item.category]?.label}
                                </p>
                                <div className="item-stats">
                                    <span className="availability">
                                        {item.quantity_available}/{item.quantity_total} disponibles
                                    </span>
                                    <span className="price">
                                        {formatCurrency(item.daily_rate)}/día
                                    </span>
                                </div>
                                <div className="item-actions">
                                    <button
                                        onClick={() => openModal('item', item)}
                                        className="btn-icon"
                                    >
                                        <Edit size={16} />
                                    </button>
                                    <button
                                        onClick={() => handleDeleteItem(item.id)}
                                        className="btn-icon danger"
                                    >
                                        <Trash2 size={16} />
                                    </button>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            {/* Sección de Alquileres */}
            <div className="section">
                <div className="section-header">
                    <h2>Alquileres Activos</h2>
                    <button
                        className="btn btn-primary"
                        onClick={() => openModal('rental')}
                    >
                        <Plus size={20} />
                        Nuevo Alquiler
                    </button>
                </div>

                <div className="rentals-table">
                    <table>
                        <thead>
                        <tr>
                            <th>Artículo</th>
                            <th>Cliente</th>
                            <th>Período</th>
                            <th>Total</th>
                            <th>Pagado</th>
                            <th>Estado</th>
                            <th>Acciones</th>
                        </tr>
                        </thead>
                        <tbody>
                        {rentals.filter(r => r.status === 'activo').map(rental => (
                            <tr key={rental.id}>
                                <td>{rental.item_name}</td>
                                <td>{rental.customer_name}</td>
                                <td>{formatDate(rental.rental_start)} - {formatDate(rental.rental_end)}</td>
                                <td>{formatCurrency(rental.total_amount)}</td>
                                <td>{formatCurrency(rental.total_paid || 0)}</td>
                                <td>
                                        <span className={`status ${rental.is_overdue ? 'overdue' : 'active'}`}>
                                            {rental.is_overdue ? 'Vencido' : 'Activo'}
                                        </span>
                                </td>
                                <td>
                                    <button
                                        className="btn-icon"
                                        onClick={() => openModal('return', rental)}
                                        title="Registrar devolución"
                                    >
                                        <RotateCcw size={16} />
                                    </button>
                                </td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
            </div>

            {/* Modal de Artículo */}
            {modals.item && (
                <div className="modal-overlay" onClick={() => closeModal('item')}>
                    <div className="modal-content" onClick={e => e.stopPropagation()}>
                        <div className="modal-header">
                            <h2>{editingItem ? 'Editar Artículo' : 'Nuevo Artículo'}</h2>
                            <button className="close-btn" onClick={() => closeModal('item')}>
                                <X size={24} />
                            </button>
                        </div>

                        <form onSubmit={handleItemSubmit}>
                            <div className="form-group">
                                <label>Nombre del artículo *</label>
                                <input
                                    type="text"
                                    className="form-control"
                                    value={itemForm.name}
                                    onChange={(e) => setItemForm({...itemForm, name: e.target.value})}
                                    required
                                />
                            </div>

                            <div className="form-row">
                                <div className="form-group">
                                    <label>Categoría</label>
                                    <select
                                        className="form-control"
                                        value={itemForm.category}
                                        onChange={(e) => setItemForm({...itemForm, category: e.target.value})}
                                    >
                                        {Object.entries(CATEGORIES).map(([key, cat]) => (
                                            <option key={key} value={key}>
                                                {cat.icon} {cat.label}
                                            </option>
                                        ))}
                                    </select>
                                </div>

                                <div className="form-group">
                                    <label>Cantidad</label>
                                    <input
                                        type="number"
                                        className="form-control"
                                        value={itemForm.quantity}
                                        onChange={(e) => setItemForm({...itemForm, quantity: parseInt(e.target.value)})}
                                        min="1"
                                        required
                                    />
                                </div>
                            </div>

                            <div className="form-group">
                                <label>Precio de alquiler (por día)</label>
                                <input
                                    type="number"
                                    className="form-control"
                                    value={itemForm.rental_price}
                                    onChange={(e) => setItemForm({...itemForm, rental_price: parseFloat(e.target.value)})}
                                    step="0.01"
                                    min="0"
                                    required
                                />
                            </div>

                            <div className="form-group">
                                <label>Descripción</label>
                                <textarea
                                    className="form-control"
                                    value={itemForm.description}
                                    onChange={(e) => setItemForm({...itemForm, description: e.target.value})}
                                    rows="3"
                                />
                            </div>

                            <div className="form-group">
                                <label>URL de imagen (opcional)</label>
                                <input
                                    type="url"
                                    className="form-control"
                                    value={itemForm.image_url}
                                    onChange={(e) => setItemForm({...itemForm, image_url: e.target.value})}
                                    placeholder="https://ejemplo.com/imagen.png"
                                />
                            </div>

                            <div className="form-actions">
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={() => closeModal('item')}
                                >
                                    Cancelar
                                </button>
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    disabled={loading}
                                >
                                    {loading ? 'Guardando...' : 'Guardar'}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {/* Modal de Alquiler */}
            {modals.rental && (
                <div className="modal-overlay" onClick={() => closeModal('rental')}>
                    <div className="modal-content large" onClick={e => e.stopPropagation()}>
                        <div className="modal-header">
                            <h2>Nuevo Alquiler</h2>
                            <button className="close-btn" onClick={() => closeModal('rental')}>
                                <X size={24} />
                            </button>
                        </div>

                        <form onSubmit={handleRentalSubmit}>
                            {/* Selección de artículo */}
                            <div className="form-group">
                                <label>Artículo a alquilar *</label>
                                <select
                                    className="form-control"
                                    value={rentalForm.item_id}
                                    onChange={(e) => setRentalForm({...rentalForm, item_id: e.target.value})}
                                    required
                                >
                                    <option value="">Seleccione un artículo...</option>
                                    {items.filter(item => item.quantity_available > 0).map(item => (
                                        <option key={item.id} value={item.id}>
                                            {item.name} ({item.quantity_available} disponibles)
                                        </option>
                                    ))}
                                </select>
                            </div>

                            {/* Información del cliente */}
                            <div className="form-section">
                                <h3>Información del Cliente</h3>
                                <div className="form-row">
                                    <div className="form-group">
                                        <label>Nombre completo *</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={rentalForm.customer_name}
                                            onChange={(e) => setRentalForm({...rentalForm, customer_name: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Cédula de identidad *</label>
                                        <input
                                            type="text"
                                            className="form-control"
                                            value={rentalForm.customer_id_number}
                                            onChange={(e) => setRentalForm({...rentalForm, customer_id_number: e.target.value})}
                                            required
                                        />
                                    </div>
                                </div>

                                <div className="form-row">
                                    <div className="form-group">
                                        <label>Teléfono *</label>
                                        <input
                                            type="tel"
                                            className="form-control"
                                            value={rentalForm.customer_phone}
                                            onChange={(e) => setRentalForm({...rentalForm, customer_phone: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Email</label>
                                        <input
                                            type="email"
                                            className="form-control"
                                            value={rentalForm.customer_email}
                                            onChange={(e) => setRentalForm({...rentalForm, customer_email: e.target.value})}
                                        />
                                    </div>
                                </div>

                                <div className="form-group">
                                    <label>Dirección</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        value={rentalForm.customer_address}
                                        onChange={(e) => setRentalForm({...rentalForm, customer_address: e.target.value})}
                                    />
                                </div>
                            </div>

                            {/* Detalles del alquiler */}
                            <div className="form-section">
                                <h3>Detalles del Alquiler</h3>
                                <div className="form-row">
                                    <div className="form-group">
                                        <label>Fecha inicio *</label>
                                        <input
                                            type="date"
                                            className="form-control"
                                            value={rentalForm.rental_start}
                                            onChange={(e) => setRentalForm({...rentalForm, rental_start: e.target.value})}
                                            required
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Fecha fin *</label>
                                        <input
                                            type="date"
                                            className="form-control"
                                            value={rentalForm.rental_end}
                                            onChange={(e) => setRentalForm({...rentalForm, rental_end: e.target.value})}
                                            min={rentalForm.rental_start}
                                            required
                                        />
                                    </div>
                                    <div className="form-group">
                                        <label>Cantidad</label>
                                        <input
                                            type="number"
                                            className="form-control"
                                            value={rentalForm.quantity_rented}
                                            onChange={(e) => setRentalForm({...rentalForm, quantity_rented: parseInt(e.target.value)})}
                                            min="1"
                                            required
                                        />
                                    </div>
                                </div>

                                <div className="form-group">
                                    <label>Depósito inicial</label>
                                    <input
                                        type="number"
                                        className="form-control"
                                        value={rentalForm.initial_deposit}
                                        onChange={(e) => setRentalForm({...rentalForm, initial_deposit: parseFloat(e.target.value)})}
                                        step="0.01"
                                        min="0"
                                    />
                                </div>

                                <div className="form-group">
                                    <label>Notas</label>
                                    <textarea
                                        className="form-control"
                                        value={rentalForm.notes}
                                        onChange={(e) => setRentalForm({...rentalForm, notes: e.target.value})}
                                        rows="3"
                                    />
                                </div>
                            </div>

                            <div className="form-actions">
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={() => closeModal('rental')}
                                >
                                    Cancelar
                                </button>
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    disabled={loading}
                                >
                                    {loading ? 'Procesando...' : 'Crear Alquiler'}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}

            {/* Modal de Devolución (simplificado para brevedad) */}
            {modals.return && selectedRental && (
                <div className="modal-overlay" onClick={() => closeModal('return')}>
                    <div className="modal-content" onClick={e => e.stopPropagation()}>
                        <div className="modal-header">
                            <h2>Registrar Devolución</h2>
                            <button className="close-btn" onClick={() => closeModal('return')}>
                                <X size={24} />
                            </button>
                        </div>

                        <form onSubmit={handleReturnSubmit}>
                            <div className="rental-summary">
                                <h3>{selectedRental.item_name}</h3>
                                <p>Cliente: {selectedRental.customer_name}</p>
                                <p>Período: {formatDate(selectedRental.rental_start)} - {formatDate(selectedRental.rental_end)}</p>
                            </div>

                            <div className="form-group">
                                <label>Estado del artículo</label>
                                <select
                                    className="form-control"
                                    value={returnForm.condition}
                                    onChange={(e) => setReturnForm({...returnForm, condition: e.target.value})}
                                >
                                    <option value="bueno">Bueno</option>
                                    <option value="regular">Regular</option>
                                    <option value="dañado">Dañado</option>
                                </select>
                            </div>

                            <div className="form-group">
                                <label>Notas sobre el estado</label>
                                <textarea
                                    className="form-control"
                                    value={returnForm.notes}
                                    onChange={(e) => setReturnForm({...returnForm, notes: e.target.value})}
                                    rows="3"
                                />
                            </div>

                            <div className="form-actions">
                                <button
                                    type="button"
                                    className="btn btn-secondary"
                                    onClick={() => closeModal('return')}
                                >
                                    Cancelar
                                </button>
                                <button
                                    type="submit"
                                    className="btn btn-primary"
                                    disabled={loading}
                                >
                                    {loading ? 'Procesando...' : 'Confirmar Devolución'}
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default RentalSystem;
